import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const location = useLocation();
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="logo">
        <Link to="/">
          <span className="code-bracket">&lt;</span>
          CodeBattleHQ
          <span className="code-bracket">/&gt;</span>
        </Link>
      </div>

      <div className="nav-links">
        <Link to="/" className={location.pathname === '/' ? 'active' : ''}>
          <i className="nav-icon home-icon"></i>
          Home
        </Link>
        <Link to="/battles" className={location.pathname === '/battles' ? 'active' : ''}>
          <i className="nav-icon battle-icon"></i>
          Coding Battles
        </Link>
        <Link to="/learning" className={location.pathname === '/learning' ? 'active' : ''}>
          <i className="nav-icon learn-icon"></i>
          Learning Paths
        </Link>
        <Link to="/achievements" className={location.pathname === '/achievements' ? 'active' : ''}>
          <i className="nav-icon trophy-icon"></i>
          Achievements
        </Link>
        <Link to="/jobs" className={location.pathname === '/jobs' ? 'active' : ''}>
          <i className="nav-icon job-icon"></i>
          Job Board
        </Link>
      </div>

      <div className="auth-buttons">
        <button className="login-btn">Log In</button>
        <button className="signup-btn">Sign Up</button>
      </div>
    </nav>
  );
}

export default Navbar;
