import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ParticleCanvas3D from '../components/ParticleCanvas3D';
import PremiumButton from '../components/PremiumButton';
import PremiumCard from '../components/PremiumCard';
import AnimatedText from '../components/AnimatedText';
import PremiumCodeEditor from '../components/PremiumCodeEditor';
import CursorEffect from '../components/CursorEffect';
import './HomePage.css';
import '../styles/designSystem.css';
import '../styles/premiumAnimations.css';

// Sample code for the code editor
const sampleCode = `function findOptimalSolution(array) {
  // Initialize variables
  let result = [];
  let current = 0;
  let best = -Infinity;

  // Iterate through the array
  for (let i = 0; i < array.length; i++) {
    // Update current sum
    current = Math.max(array[i], current + array[i]);

    // Update best result if needed
    best = Math.max(best, current);

    // Add to result if it improves solution
    if (current > 0) {
      result.push(array[i]);
    }
  }

  // Return the optimal solution
  return { result, maxSum: best };
}`;

function HomePage() {
  const [showDemo, setShowDemo] = useState(false);
  const [cursorActive, setCursorActive] = useState(false);
  const [typingComplete, setTypingComplete] = useState(false);
  const [activeSection, setActiveSection] = useState('hero');
  const navigate = useNavigate();

  // Enable custom cursor effect
  useEffect(() => {
    document.body.classList.add('custom-cursor-active');
    setCursorActive(true);

    // Add scroll event listener for section animations
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;

      // Get sections
      const featuresSection = document.querySelector('.features-section');
      const learningPathsSection = document.querySelector('.learning-paths-section');

      // Determine active section based on scroll position
      if (featuresSection && scrollPosition >= featuresSection.offsetTop - windowHeight / 2) {
        setActiveSection('features');
      } else if (learningPathsSection && scrollPosition >= learningPathsSection.offsetTop - windowHeight / 2) {
        setActiveSection('learning-paths');
      } else {
        setActiveSection('hero');
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      document.body.classList.remove('custom-cursor-active');
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleWatchDemo = () => {
    setShowDemo(true);
  };

  const handleCloseDemo = () => {
    setShowDemo(false);
  };

  const handleGetStarted = () => {
    navigate('/battle/practice');
  };

  const handleTypingComplete = () => {
    setTypingComplete(true);
  };

  return (
    <div className="home-container">
      {/* Custom cursor effect */}
      {cursorActive && <CursorEffect />}

      {/* Hero section with 3D particle background */}
      <div className="hero-section">
        <ParticleCanvas3D
          particleCount={150}
          particleColor="rgba(99, 102, 241, 0.8)"
          depth={400}
          speed={0.3}
          connectParticles={true}
        />

        <div className="hero-content">
          <h1 className="hero-title">
            <AnimatedText
              text="Master Coding."
              variant="gradient"
              animation="typing"
              speed={80}
              delay={300}
              onComplete={handleTypingComplete}
            />
            <br />
            <AnimatedText
              text="Get Hired."
              variant="gradient"
              animation="typing"
              speed={80}
              delay={1500}
            />
          </h1>
          <p className={`hero-subtitle ${typingComplete ? 'animate-fade-up' : 'hidden'}`}>
            AI-driven competitive coding platform that evaluates your skills and connects you to opportunities
          </p>
          <div className={`hero-buttons ${typingComplete ? 'animate-fade-up delay-300' : 'hidden'}`}>
            <PremiumButton
              variant="primary"
              size="lg"
              onClick={handleGetStarted}
              glowEffect={true}
              hoverScale={true}
              icon={<span>üöÄ</span>}
            >
              Get Started
            </PremiumButton>

            <PremiumButton
              variant="outline"
              size="lg"
              onClick={handleWatchDemo}
              icon={<span>‚ñ∂Ô∏è</span>}
            >
              Watch Demo
            </PremiumButton>
          </div>
        </div>

        <div className="code-animation">
          <PremiumCodeEditor
            code={sampleCode}
            language="javascript"
            theme="dracula"
            animate={true}
            typingSpeed={20}
            highlightLines={[7, 10, 13, 20]}
          />
        </div>
      </div>

      <section className="features-section">
        <h2 className="animated-gradient">Why Choose AlgoHunt?</h2>
        <div className="features-grid">
          <div className="feature-card" data-aos="fade-up" data-aos-delay="100">
            <div className="feature-icon">
              <div className="icon-container">
                <span className="icon-symbol">üß†</span>
                <div className="icon-glow"></div>
              </div>
            </div>
            <h3>AI-Powered Matchmaking</h3>
            <p>Our advanced AI matches you with opponents at your skill level for fair and challenging battles</p>
            <div className="feature-hover-effect"></div>
          </div>
          <div className="feature-card" data-aos="fade-up" data-aos-delay="200">
            <div className="feature-icon">
              <div className="icon-container">
                <span className="icon-symbol">üìä</span>
                <div className="icon-glow"></div>
              </div>
            </div>
            <h3>Skill Assessment</h3>
            <p>Get detailed insights about your coding strengths and areas for improvement</p>
            <div className="feature-hover-effect"></div>
          </div>
          <div className="feature-card" data-aos="fade-up" data-aos-delay="300">
            <div className="feature-icon">
              <div className="icon-container">
                <span className="icon-symbol">üèÜ</span>
                <div className="icon-glow"></div>
              </div>
            </div>
            <h3>Competitive Tournaments</h3>
            <p>Participate in regular coding tournaments with prizes and recognition</p>
            <div className="feature-hover-effect"></div>
          </div>
          <div className="feature-card" data-aos="fade-up" data-aos-delay="400">
            <div className="feature-icon">
              <div className="icon-container">
                <span className="icon-symbol">üíº</span>
                <div className="icon-glow"></div>
              </div>
            </div>
            <h3>Job Opportunities</h3>
            <p>Connect directly with companies looking for your specific coding skills</p>
            <div className="feature-hover-effect"></div>
          </div>
        </div>
      </section>

      {/* Learning Paths Section */}
      <section className="learning-paths-section">
        <h2 className="animated-gradient">Learning Paths</h2>
        <div className="learning-paths-grid">
          <div className="learning-path-card" data-aos="flip-left" data-aos-delay="100">
            <div className="path-icon">
              <div className="icon-container">
                <span className="icon-symbol">üìö</span>
                <div className="icon-glow blue"></div>
              </div>
            </div>
            <h3>Data Structures</h3>
            <p>Master essential data structures like arrays, linked lists, trees, and graphs</p>
            <button className="explore-path-btn" onClick={() => navigate('/learning')}>
              Explore
              <span className="btn-arrow">‚Üí</span>
            </button>
            <div className="card-shine"></div>
          </div>
          <div className="learning-path-card" data-aos="flip-left" data-aos-delay="200">
            <div className="path-icon">
              <div className="icon-container">
                <span className="icon-symbol">üßÆ</span>
                <div className="icon-glow purple"></div>
              </div>
            </div>
            <h3>Algorithms</h3>
            <p>Learn sorting, searching, dynamic programming, and graph algorithms</p>
            <button className="explore-path-btn" onClick={() => navigate('/learning')}>
              Explore
              <span className="btn-arrow">‚Üí</span>
            </button>
            <div className="card-shine"></div>
          </div>
          <div className="learning-path-card" data-aos="flip-left" data-aos-delay="300">
            <div className="path-icon">
              <div className="icon-container">
                <span className="icon-symbol">üîç</span>
                <div className="icon-glow green"></div>
              </div>
            </div>
            <h3>Problem Solving</h3>
            <p>Develop your problem-solving skills with real-world coding challenges</p>
            <button className="explore-path-btn" onClick={() => navigate('/learning')}>
              Explore
              <span className="btn-arrow">‚Üí</span>
            </button>
            <div className="card-shine"></div>
          </div>
        </div>
      </section>

      {/* Demo Modal */}
      {showDemo && (
        <div className="demo-modal-overlay">
          <div className="demo-modal">
            <button className="close-demo-btn" onClick={handleCloseDemo}>√ó</button>
            <h2>Welcome to AlgoHunt!</h2>

            <div className="demo-steps">
              <div className="demo-step">
                <div className="step-number">1</div>
                <div className="step-content">
                  <h3>Choose Your Battle</h3>
                  <p>Select from 1v1 battles, tournaments, or practice mode to improve your skills</p>
                  <img src="/images/demo/battle-selection.png" alt="Battle Selection" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">2</div>
                <div className="step-content">
                  <h3>Solve Coding Challenges</h3>
                  <p>Use our professional code editor with syntax highlighting and auto-completion</p>
                  <img src="/images/demo/code-editor.png" alt="Code Editor" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">3</div>
                <div className="step-content">
                  <h3>Track Your Progress</h3>
                  <p>Monitor your performance against opponents in real-time</p>
                  <img src="/images/demo/progress-tracking.png" alt="Progress Tracking" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">4</div>
                <div className="step-content">
                  <h3>Earn Achievements</h3>
                  <p>Unlock badges and climb the global leaderboard as you improve</p>
                  <img src="/images/demo/achievements.png" alt="Achievements" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">5</div>
                <div className="step-content">
                  <h3>Get Hired</h3>
                  <p>Connect with employers looking for developers with your specific skills</p>
                  <img src="/images/demo/job-board.png" alt="Job Board" className="demo-image" />
                </div>
              </div>
            </div>

            <div className="demo-actions">
              <button className="try-now-btn" onClick={() => {
                handleCloseDemo();
                navigate('/battle/practice');
              }}>Try Practice Mode Now</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default HomePage;
