sourcesContent:
import React, { useState, useEffect, useRef } from 'react';
import './CodingBattle.css';
import { useParams, useNavigate } from 'react-router-dom';
// import Editor from '@monaco-editor/react';

function CodingBattle({ isDemoMode = false }) {
  const { battleId } = useParams();
  const navigate = useNavigate();
  const editorRef = useRef(null);
  const [currentView, setCurrentView] = useState('battle'); // 'main' or 'battle'

  // Battle state
  const [battleState, setBattleState] = useState({
    status: isDemoMode ? 'active' : 'waiting', // waiting, preparing, active, completed
    timeRemaining: 1800, // 30 minutes in seconds
    problem: null,
    opponent: null,
    userCode: '',
    opponentProgress: 0,
    userTestResults: null,
    winner: null,
    battleType: '1v1',
    aiAnalysis: null,
    skillMatchPercentage: null
  });

  // UI states
  const [testCases, setTestCases] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('javascript');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showHint, setShowHint] = useState(false);
  const [showAiInsights, setShowAiInsights] = useState(false);
  const [compilerOutput, setCompilerOutput] = useState(null);
  const [activeTab, setActiveTab] = useState('problem'); // problem, submissions, leaderboard
  const [runningTest, setRunningTest] = useState(false);
  const [testResults, setTestResults] = useState([]);

  // Matchmaking states
  const [showMatchmakingModal, setShowMatchmakingModal] = useState(false);
  const [matchmaking, setMatchmaking] = useState(false);
  const [queuePosition, setQueuePosition] = useState(0);
  const [usersInQueue, setUsersInQueue] = useState(0);
  const [matchStartError, setMatchStartError] = useState(null);
  const [matchFound, setMatchFound] = useState(false);
  const [selectedDifficulty, setSelectedDifficulty] = useState('medium');
  const [selectedCategory, setSelectedCategory] = useState('algorithms');

  // Language settings
  const languageExtensions = {
    javascript: 'js',
    python: 'py',
    java: 'java',
    cpp: 'cpp',
    csharp: 'cs'
  };

  // Editor themes by language
  const editorThemes = {
    javascript: 'vs-dark',
    python: 'vs-dark',
    java: 'vs-dark',
    cpp: 'vs-dark',
    csharp: 'vs-dark'
  };

  // Initialize demo mode if specified
  useEffect(() => {
    if (isDemoMode) {
      startDemoMode();
    } else if (battleId) {
      // If joining an existing battle
      fetchBattleDetails(battleId);
    }

    // Add a prominent Practice Mode button to the page
    const addPracticeButton = () => {
      // Remove any existing practice button first
      const existingButton = document.getElementById('floating-practice-button');
      if (existingButton) {
        existingButton.remove();
      }

      // Create new button
      const practiceButton = document.createElement('button');
      practiceButton.id = 'floating-practice-button';
      practiceButton.innerHTML = '🏋️ PRACTICE MODE';
      practiceButton.onclick = startDemoMode;

      // Add to body
      document.body.appendChild(practiceButton);
    };

    // Call the function to add the button
    if (!isDemoMode) {
      addPracticeButton();
    }

    // Clean up on unmount
    return () => {
      const button = document.getElementById('floating-practice-button');
      if (button) {
        button.remove();
      }
    };
  }, [isDemoMode, battleId]);

  // Fetch battle details if joining an existing battle
  const fetchBattleDetails = async (id) => {
    try {
      console.log(`Fetching battle details for ID: ${id}`);
      // In a real app, this would make an API call
      // For now, we'll just simulate a battle
      startDemoMode();
    } catch (error) {
      console.error("Error fetching battle details:", error);
      setMatchStartError("Failed to load battle details. Please try again.");
    }
  };

  // Start matchmaking process
  const startMatchmaking = () => {
    setMatchmaking(true);
    setMatchStartError(null);

    // Simulate matchmaking
    setTimeout(() => {
      setMatchFound(true);
      setQueuePosition(0);
    }, 3000);
  };

  // Accept the match and start the battle
  const acceptMatch = () => {
    setMatchFound(false);
    setMatchmaking(false);
    setShowMatchmakingModal(false);

    // Start a demo battle for now
    startDemoMode();
  };

  // Decline the match and return to matchmaking
  const declineMatch = () => {
    setMatchFound(false);
    setMatchmaking(false);
    setMatchStartError(null);
  };

  // Start demo mode with AI opponent
  const startDemoMode = () => {
    console.log("Starting Practice Mode...");

    // Hide matchmaking modal if open
    setShowMatchmakingModal(false);

    // Initialize a demo battle with a sample problem
    setBattleState(prev => ({
      ...prev,
      status: 'active',
      problem: {
        id: 1,
        title: "Two Sum",
        description: "Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.\n\nYou may assume that each input would have exactly one solution, and you may not use the same element twice.\n\nYou can return the answer in any order.",
        difficulty: selectedDifficulty || 'medium',
        category: selectedCategory || 'algorithms',
        starterCode: {
          javascript: "/**\n * @param {number[]} nums\n * @param {number} target\n * @return {number[]}\n */\nfunction twoSum(nums, target) {\n  // Your code here\n}",
          python: "def two_sum(nums, target):\n    # Your code here\n    pass",
          java: "class Solution {\n    public int[] twoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}",
          cpp: "#include <vector>\n\nclass Solution {\npublic:\n    std::vector<int> twoSum(std::vector<int>& nums, int target) {\n        // Your code here\n        return {0, 0};\n    }\n};",
          csharp: "public class Solution {\n    public int[] TwoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}"
        }
      },
      opponent: {
        name: "AI Opponent",
        rating: 1800,
        winRate: "75%",
        preferredLanguage: "Python",
        skillLevel: "Adaptive",
        avatar: "https://randomuser.me/api/portraits/lego/1.jpg"
      },
      userCode: getDefaultStarterCode(selectedLanguage || 'javascript')
    }));

    // Set demo test cases
    setTestCases([
      {
        id: 1,
        input: "[2, 7, 11, 15], 9",
        expectedOutput: "[0,1]"
      },
      {
        id: 2,
        input: "[3, 2, 4], 6",
        expectedOutput: "[1,2]"
      },
      {
        id: 3,
        input: "[3, 3], 6",
        expectedOutput: "[0,1]"
      }
    ]);

    // Simulate AI opponent progress updates
    startAIOpponentSimulation();
  };

  // Simulate AI opponent progress
  const startAIOpponentSimulation = () => {
    let progress = 0;
    const interval = setInterval(() => {
      progress += Math.random() * 5;
      if (progress >= 100) {
        progress = 100;
        clearInterval(interval);

        // If user hasn't completed yet, AI wins
        if (battleState.winner === null) {
          setTimeout(() => {
            setBattleState(prev => ({
              ...prev,
              status: 'completed',
              winner: 'opponent',
              aiAnalysis: "The AI opponent completed the challenge faster. Try optimizing your solution for better performance."
            }));
          }, 2000);
        }
      }

      setBattleState(prev => ({
        ...prev,
        opponentProgress: progress
      }));
    }, 3000);
  };

  // Get default starter code for selected language
  const getDefaultStarterCode = (language) => {
    if (!language) return '';

    const defaultCode = {
      javascript: "/**\n * @param {number[]} nums\n * @param {number} target\n * @return {number[]}\n */\nfunction twoSum(nums, target) {\n  // Your code here\n}",
      python: "def two_sum(nums, target):\n    # Your code here\n    pass",
      java: "class Solution {\n    public int[] twoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}",
      cpp: "#include <vector>\n\nclass Solution {\npublic:\n    std::vector<int> twoSum(std::vector<int>& nums, int target) {\n        // Your code here\n        return {0, 0};\n    }\n};",
      csharp: "public class Solution {\n    public int[] TwoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}"
    };

    return defaultCode[language] || '';
  };

  // Handle language change
  const handleLanguageChange = (language) => {
    setSelectedLanguage(language);

    // Update editor content with starter code for the selected language
    if (editorRef.current && battleState.problem) {
      const starterCode = battleState.problem.starterCode[language] || getDefaultStarterCode(language);
      // In a real app, you would set the editor value here
      // editorRef.current.setValue(starterCode);

      setBattleState(prev => ({
        ...prev,
        userCode: starterCode
      }));
    }
  };

  // Handle editor mount
  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
  };

  // Handle editor change
  const handleEditorChange = (value) => {
    setBattleState(prev => ({
      ...prev,
      userCode: value
    }));
  };

  // Run code against a single test case
  const runTestCase = async (testCaseId) => {
    setRunningTest(true);
    setCompilerOutput(null);

    try {
      // Simulate code execution
      await new Promise(resolve => setTimeout(resolve, 1500));

      const testCase = testCases.find(tc => tc.id === testCaseId);

      if (!testCase) {
        throw new Error('Test case not found');
      }

      // Simulate test result (random success/failure)
      const passed = Math.random() > 0.3;

      setCompilerOutput({
        status: passed ? 'success' : 'error',
        message: passed ? 'Test passed!' : 'Test failed',
        output: passed ? testCase.expectedOutput : 'Incorrect output'
      });
    } catch (error) {
      console.error('Error running test case:', error);
      setCompilerOutput({
        status: 'error',
        message: 'Failed to run test case',
        output: error.message
      });
    } finally {
      setRunningTest(false);
    }
  };

  // Submit code for testing all test cases
  const submitCode = async () => {
    setIsSubmitting(true);
    setCompilerOutput(null);

    try {
      // Simulate code submission
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Generate random test results
      const results = testCases.map(testCase => ({
        testCaseId: testCase.id,
        passed: Math.random() > 0.3,
        output: Math.random() > 0.3 ? testCase.expectedOutput : 'Incorrect output'
      }));

      setTestResults(results);

      const allPassed = results.every(r => r.passed);

      if (allPassed) {
        // User completed all tests
        setBattleState(prev => ({
          ...prev,
          status: prev.opponentProgress >= 100 ? 'completed' : prev.status,
          winner: prev.opponentProgress >= 100 ? 'opponent' : 'user',
          aiAnalysis: prev.opponentProgress >= 100 
            ? "Great job! However, the AI opponent completed the challenge slightly faster."
            : "Congratulations! You solved the problem before the AI opponent."
        }));
      }
    } catch (error) {
      console.error('Error submitting code:', error);
      setCompilerOutput({
        status: 'error',
        message: 'Failed to submit code',
        output: error.message
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  // Prepare code with test case
  const prepareCodeWithTestCase = (code, testCase) => {
    // This would be implemented based on the language
    return code;
  };

  // Render main menu
  const renderMainMenu = () => {
    return (
      <div className="coding-battle-main-menu">
        <h1>CodeArena Battles</h1>
        <p className="main-subtitle">Challenge yourself and others in real-time coding competitions</p>

        <div className="battle-mode-cards">
          <div className="mode-card" onClick={() => setShowMatchmakingModal(true)}>
            <div className="mode-icon">🏆</div>
            <h2>1v1 Battle</h2>
            <p>Compete head-to-head against another coder with AI-powered matchmaking</p>
            <button className="mode-button battle">Find Match</button>
          </div>

          <div className="mode-card" onClick={startDemoMode}>
            <div className="mode-icon">🏋️</div>
            <h2>Practice Mode</h2>
            <p>Sharpen your skills against an AI opponent at your own pace</p>
            <button className="mode-button practice">Start Practice</button>
          </div>
        </div>

        <div className="battle-stats">
          <div className="stat">
            <span className="stat-number">1,245</span>
            <span className="stat-label">Active Battles</span>
          </div>
          <div className="stat">
            <span className="stat-number">42,891</span>
            <span className="stat-label">Battles Completed</span>
          </div>
          <div className="stat">
            <span className="stat-number">8,723</span>
            <span className="stat-label">Coders Online</span>
          </div>
        </div>
      </div>
    );
  };

  // Render matchmaking modal
  const renderMatchmakingModal = () => {
    return (
      <div className="modal-overlay">
        <div className="matchmaking-modal">
          <h2>Find a Coding Battle</h2>

          {!matchmaking ? (
            <>
              <div className="matchmaking-options">
                <div className="option-group">
                  <label>Difficulty</label>
                  <div className="difficulty-buttons">
                    <button 
                      className={selectedDifficulty === 'easy' ? 'active' : ''} 
                      onClick={() => setSelectedDifficulty('easy')}
                    >
                      Easy
                    </button>
                    <button 
                      className={selectedDifficulty === 'medium' ? 'active' : ''} 
                      onClick={() => setSelectedDifficulty('medium')}
                    >
                      Medium
                    </button>
                    <button 
                      className={selectedDifficulty === 'hard' ? 'active' : ''} 
                      onClick={() => setSelectedDifficulty('hard')}
                    >
                      Hard
                    </button>
                  </div>
                </div>

                <div className="option-group">
                  <label>Category</label>
                  <select 
                    value={selectedCategory} 
                    onChange={(e) => setSelectedCategory(e.target.value)}
                  >
                    <option value="algorithms">Algorithms</option>
                    <option value="data-structures">Data Structures</option>
                    <option value="dynamic-programming">Dynamic Programming</option>
                    <option value="math">Math</option>
                    <option value="string">String Manipulation</option>
                  </select>
                </div>

                <div className="option-group">
                  <label>Language</label>
                  <select 
                    value={selectedLanguage} 
                    onChange={(e) => handleLanguageChange(e.target.value)}
                  >
                    <option value="javascript">JavaScript</option>
                    <option value="python">Python</option>
                    <option value="java">Java</option>
                    <option value="cpp">C++</option>
                    <option value="csharp">C#</option>
                  </select>
                </div>
              </div>

              <div className="matchmaking-actions">
                <button className="find-match-button" onClick={startMatchmaking}>
                  Find Match
                </button>
                <button className="practice-button" onClick={startDemoMode}>
                  Practice Mode
                </button>
                <button className="cancel-button" onClick={() => setShowMatchmakingModal(false)}>
                  Cancel
                </button>
              </div>

              {matchStartError && (
                <div className="error-message">{matchStartError}</div>
              )}
            </>
          ) : matchFound ? (
            <div className="match-found">
              <h3>Match Found!</h3>
              <div className="opponent-info">
                <img src="https://randomuser.me/api/portraits/men/32.jpg" alt="Opponent" />
                <div className="opponent-details">
                  <h4>John Doe</h4>
                  <p>Rating: 1750</p>
                  <p>Win Rate: 68%</p>
                </div>
              </div>
              <div className="match-actions">
                <button className="accept-button" onClick={acceptMatch}>
                  Accept
                </button>
                <button className="decline-button" onClick={declineMatch}>
                  Decline
                </button>
              </div>
              <p className="match-timer">Time remaining: 15s</p>
            </div>
          ) : (
            <div className="matchmaking-progress">
              <div className="spinner"></div>
              <h3>Finding a match...</h3>
              <p>Queue position: {queuePosition}</p>
              <p>Users in queue: {usersInQueue}</p>
              <button className="cancel-button" onClick={() => setMatchmaking(false)}>
                Cancel
              </button>
            </div>
          )}
        </div>
      </div>
    );
  };

  // Render battle interface
  const renderBattleInterface = () => {
    if (battleState.status === 'waiting') {
      return (
        <div className="battle-waiting">
          <h2>Ready to Code?</h2>
          <p>Start a coding battle or practice your skills.</p>
          <div className="battle-options">
            <button className="find-match-button" onClick={() => setShowMatchmakingModal(true)}>
              Find a Match
            </button>
            <button className="practice-button" onClick={startDemoMode}>
              Practice Mode
            </button>
          </div>
        </div>
      );
    }

    if (battleState.status === 'completed') {
      return (
        <div className="battle-completed">
          <h2>{battleState.winner === 'user' ? 'Victory!' : 'Better Luck Next Time'}</h2>
          <div className="battle-result">
            <div className="result-icon">
              {battleState.winner === 'user' ? '🏆' : '😢'}
            </div>
            <p className="result-message">
              {battleState.winner === 'user' 
                ? 'Congratulations! You won the coding battle.' 
                : 'You were defeated this time, but keep practicing!'}
            </p>
          </div>

          {battleState.aiAnalysis && (
            <div className="ai-analysis">
              <h3>Battle Analysis</h3>
              <p>{battleState.aiAnalysis}</p>
            </div>
          )}

          <div className="post-battle-actions">
            <button className="new-battle-button" onClick={() => setShowMatchmakingModal(true)}>
              New Battle
            </button>
            <button className="practice-button" onClick={startDemoMode}>
              Practice Again
            </button>
            <button className="home-button" onClick={() => navigate('/battles')}>
              Back to Battles
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="battle-active">
        <div className="battle-header">
          <div className="problem-info">
            <h2>{battleState.problem?.title || 'Loading problem...'}</h2>
            <div className="problem-meta">
              <span className={`difficulty ${battleState.problem?.difficulty || 'medium'}`}>
                {battleState.problem?.difficulty || 'Medium'}
              </span>
              <span className="category">{battleState.problem?.category || 'Algorithms'}</span>
            </div>
          </div>

          <div className="battle-timer">
            <i className="far fa-clock"></i>
            <span>{Math.floor(battleState.timeRemaining / 60)}:{(battleState.timeRemaining % 60).toString().padStart(2, '0')}</span>
          </div>

          <div className="opponent-info">
            <div className="opponent-progress">
              <div className="progress-label">
                <img 
                  src={battleState.opponent?.avatar || "https://randomuser.me/api/portraits/lego/1.jpg"} 
                  alt="Opponent" 
                  className="opponent-avatar"
                />
                <span>{battleState.opponent?.name || 'AI Opponent'}</span>
              </div>
              <div className="progress-bar">
                <div 
                  className="progress-fill" 
                  style={{ width: `${battleState.opponentProgress}%` }}
                ></div>
              </div>
              <span className="progress-percentage">{Math.round(battleState.opponentProgress)}%</span>
            </div>
          </div>
        </div>

        <div className="battle-content">
          <div className="problem-panel">
            <div className="panel-tabs">
              <button 
                className={`tab-button ${activeTab === 'problem' ? 'active' : ''}`}
                onClick={() => setActiveTab('problem')}
              >
                Problem
              </button>
              <button 
                className={`tab-button ${activeTab === 'submissions' ? 'active' : ''}`}
                onClick={() => setActiveTab('submissions')}
              >
                Submissions
              </button>
              <button 
                className={`tab-button ${activeTab === 'leaderboard' ? 'active' : ''}`}
                onClick={() => setActiveTab('leaderboard')}
              >
                Leaderboard
              </button>
            </div>

            <div className="panel-content">
              {activeTab === 'problem' && (
                <div className="problem-description">
                  <p>{battleState.problem?.description || 'Loading problem description...'}</p>

                  <div className="test-cases">
                    <h3>Example Test Cases</h3>
                    {testCases.map(testCase => (
                      <div className="test-case" key={testCase.id}>
                        <div className="test-case-header">
                          <span>Test Case {testCase.id}</span>
                          <button 
                            className="run-test-button"
                            onClick={() => runTestCase(testCase.id)}
                            disabled={runningTest}
                          >
                            {runningTest ? 'Running...' : 'Run Test'}
                          </button>
                        </div>
                        <div className="test-case-content">
                          <div className="test-input">
                            <strong>Input:</strong> {testCase.input}
                          </div>
                          <div className="test-output">
                            <strong>Expected Output:</strong> {testCase.expectedOutput}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  {compilerOutput && (
                    <div className={`compiler-output ${compilerOutput.status}`}>
                      <h3>Test Result</h3>
                      <div className="output-message">{compilerOutput.message}</div>
                      <div className="output-content">{compilerOutput.output}</div>
                    </div>
                  )}
                </div>
              )}

              {activeTab === 'submissions' && (
                <div className="submissions-tab">
                  <h3>Your Submissions</h3>
                  {testResults.length > 0 ? (
                    <div className="test-results">
                      {testResults.map((result, index) => {
                        const testCase = testCases.find(tc => tc.id === result.testCaseId);
                        return (
                          <div 
                            className={`test-result ${result.passed ? 'passed' : 'failed'}`} 
                            key={index}
                          >
                            <div className="test-result-header">
                              <span>Test Case {result.testCaseId}</span>
                              <span className={`result-badge ${result.passed ? 'passed' : 'failed'}`}>
                                {result.passed ? 'Passed' : 'Failed'}
                              </span>
                            </div>
                            <div className="test-result-content">
                              <div className="test-input">
                                <strong>Input:</strong> {testCase?.input || 'Unknown input'}
                              </div>
                              <div className="test-expected">
                                <strong>Expected:</strong> {testCase?.expectedOutput || 'Unknown expected output'}
                              </div>
                              <div className="test-actual">
                                <strong>Your Output:</strong> {result.output}
                              </div>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  ) : (
                    <p className="no-submissions">No submissions yet. Run your code to see results here.</p>
                  )}
                </div>
              )}

              {activeTab === 'leaderboard' && (
                <div className="leaderboard-tab">
                  <h3>Battle Leaderboard</h3>
                  <div className="leaderboard-entries">
                    <div className="leaderboard-entry">
                      <span className="rank">1</span>
                      <img src="https://randomuser.me/api/portraits/women/32.jpg" alt="User" className="user-avatar" />
                      <span className="username">Sarah Johnson</span>
                      <span className="completion-time">12:45</span>
                    </div>
                    <div className="leaderboard-entry">
                      <span className="rank">2</span>
                      <img src="https://randomuser.me/api/portraits/men/45.jpg" alt="User" className="user-avatar" />
                      <span className="username">Michael Chen</span>
                      <span className="completion-time">14:22</span>
                    </div>
                    <div className="leaderboard-entry you">
                      <span className="rank">3</span>
                      <img src="https://randomuser.me/api/portraits/lego/2.jpg" alt="You" className="user-avatar" />
                      <span className="username">You</span>
                      <span className="completion-time">--:--</span>
                    </div>
                    <div className="leaderboard-entry">
                      <span className="rank">4</span>
                      <img src="https://randomuser.me/api/portraits/women/68.jpg" alt="User" className="user-avatar" />
                      <span className="username">Emma Rodriguez</span>
                      <span className="completion-time">--:--</span>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
          <div className="editor-panel">
            <div className="editor-container">
              <textarea
                className="code-textarea"
                value={battleState.userCode}
                onChange={(e) => handleEditorChange(e.target.value)}
                style={{ width: '100%', height: '100%', fontFamily: 'monospace' }}
              />
            </div>
            <div className="editor-actions">
              <button className="run-button" onClick={submitCode} disabled={isSubmitting}>
                {isSubmitting ? "Running..." : "Run Code"}
              </button>
              <button className="hint-button" onClick={() => setShowHint(true)}>
                Show Hint
              </button>
              <button className="insights-button" onClick={() => setShowAiInsights(true)}>
                Show AI Insights
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Render the main battle UI
  return (
    <div className="coding-battle-container">
      {battleState.status === 'waiting' && currentView === 'main' ? (
        renderMainMenu()
      ) : (
        renderBattleInterface()
      )}

      {showMatchmakingModal && renderMatchmakingModal()}

      {showHint && (
        <div className="modal-overlay">
          <div className="hint-modal">
            <h3>Hint</h3>
            <p>Consider using a hash map to store the numbers you've seen so far. For each number, check if its complement (target - current number) exists in the hash map.</p>
            <button className="close-button" onClick={() => setShowHint(false)}>Close</button>
          </div>
        </div>
      )}

      {showAiInsights && (
        <div className="modal-overlay">
          <div className="ai-insights-modal">
            <h3>AI Insights</h3>
            <p>Here are some insights from the AI:</p>
            <ul>
              <li>Try using a hash map to store the numbers you've seen so far.</li>
              <li>For each number, check if its complement (target - current number) exists in the hash map.</li>
              <li>Consider using a hash map to store the numbers you've seen so far.</li>
              <li>For each number, check if its complement (target - current number) exists in the hash map.</li>
            </ul>
            <button className="close-button" onClick={() => setShowAiInsights(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default CodingBattle;

// Helper function to get default starter code based on language
const getDefaultStarterCode = (language) => {
  const defaultCode = {
    javascript: "/**\n * @param {number[]} nums\n * @param {number} target\n * @return {number[]}\n */\nfunction twoSum(nums, target) {\n  // Your code here\n}",
    python: "def two_sum(nums, target):\n    # Your code here\n    pass",
    java: "class Solution {\n    public int[] twoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}",
    cpp: "#include <vector>\n\nclass Solution {\npublic:\n    std::vector<int> twoSum(std::vector<int>& nums, int target) {\n        // Your code here\n        return {0, 0};\n    }\n};",
    csharp: "public class Solution {\n    public int[] TwoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}"
  };

  return defaultCode[language] || defaultCode.javascript;
};
