import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import TypingAnimation from '../components/TypingAnimation';
import CodeAnimationEffect from '../components/CodeAnimationEffect';
import ParticleBackground from '../components/ParticleBackground';
import CursorEffect from '../components/CursorEffect';
import './HomePage.css';

function HomePage() {
  const [showDemo, setShowDemo] = useState(false);
  const [cursorActive, setCursorActive] = useState(false);
  const [typingComplete, setTypingComplete] = useState(false);
  const navigate = useNavigate();

  // Enable custom cursor effect
  useEffect(() => {
    document.body.classList.add('custom-cursor-active');
    setCursorActive(true);

    return () => {
      document.body.classList.remove('custom-cursor-active');
    };
  }, []);

  const handleWatchDemo = () => {
    setShowDemo(true);
  };

  const handleCloseDemo = () => {
    setShowDemo(false);
  };

  const handleGetStarted = () => {
    navigate('/battle/practice');
  };

  const handleTypingComplete = () => {
    setTypingComplete(true);
  };

  return (
    <div className="home-container">
      <div className="hero-section">
        <div className="hero-content">
          <h1>
            Master
            Coding.
            Get
            Hired.
          </h1>
          <p>
            AI-driven
            competitive coding
            platform that
            evaluates your skills
            and connects you to
            opportunities
          </p>
          <div className="hero-buttons">
            <button className="get-started-btn" onClick={handleGetStarted}>Get Started</button>
            <button className="watch-demo-btn" onClick={handleWatchDemo}>Watch Demo</button>
          </div>
        </div>

        <div className="code-animation">
          <div className="code-challenge">
            <div className="challenge-text">challenge.js</div>
            <pre>
              <code>
{`function findOptimalSolution(array) {
  let result = [];
  let current = 0;
  for (let i = 0; i < array.length; i++) {
    // Implement your solution here
    current += array[i];
  }
}`}
              </code>
            </pre>
          </div>
        </div>
      </div>

      <section className="features-section">
        <h2>Why Choose AlgoHunt?</h2>
        <div className="features-grid">
          <div className="feature-card">
            <div className="feature-icon">🧠</div>
            <h3>AI-Powered Matchmaking</h3>
            <p>Our advanced AI matches you with opponents at your skill level for fair and challenging battles</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">📊</div>
            <h3>Skill Assessment</h3>
            <p>Get detailed insights about your coding strengths and areas for improvement</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">🏆</div>
            <h3>Competitive Tournaments</h3>
            <p>Participate in regular coding tournaments with prizes and recognition</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">💼</div>
            <h3>Job Opportunities</h3>
            <p>Connect directly with companies looking for your specific coding skills</p>
          </div>
        </div>
      </section>

      {/* Demo Modal */}
      {showDemo && (
        <div className="demo-modal-overlay">
          <div className="demo-modal">
            <button className="close-demo-btn" onClick={handleCloseDemo}>×</button>
            <h2>Welcome to AlgoHunt!</h2>

            <div className="demo-steps">
              <div className="demo-step">
                <div className="step-number">1</div>
                <div className="step-content">
                  <h3>Choose Your Battle</h3>
                  <p>Select from 1v1 battles, tournaments, or practice mode to improve your skills</p>
                  <img src="/images/demo/battle-selection.png" alt="Battle Selection" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">2</div>
                <div className="step-content">
                  <h3>Solve Coding Challenges</h3>
                  <p>Use our professional code editor with syntax highlighting and auto-completion</p>
                  <img src="/images/demo/code-editor.png" alt="Code Editor" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">3</div>
                <div className="step-content">
                  <h3>Track Your Progress</h3>
                  <p>Monitor your performance against opponents in real-time</p>
                  <img src="/images/demo/progress-tracking.png" alt="Progress Tracking" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">4</div>
                <div className="step-content">
                  <h3>Earn Achievements</h3>
                  <p>Unlock badges and climb the global leaderboard as you improve</p>
                  <img src="/images/demo/achievements.png" alt="Achievements" className="demo-image" />
                </div>
              </div>

              <div className="demo-step">
                <div className="step-number">5</div>
                <div className="step-content">
                  <h3>Get Hired</h3>
                  <p>Connect with employers looking for developers with your specific skills</p>
                  <img src="/images/demo/job-board.png" alt="Job Board" className="demo-image" />
                </div>
              </div>
            </div>

            <div className="demo-actions">
              <button className="try-now-btn" onClick={() => {
                handleCloseDemo();
                navigate('/battle/practice');
              }}>Try Practice Mode Now</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default HomePage;
