import React, { useState, useEffect, useRef } from 'react';
import './CodingBattle.css';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';
import Editor from '@monaco-editor/react';

function CodingBattle() {
  const { battleId } = useParams();
  const navigate = useNavigate();
  const socketRef = useRef(null);
  const editorRef = useRef(null);

  // Battle state
  const [battleState, setBattleState] = useState({
    status: 'preparing', // preparing, matchmaking, active, completed
    timeRemaining: 1800, // 30 minutes in seconds
    problem: null,
    opponent: null,
    userCode: '',
    opponentProgress: 0,
    userTestResults: null,
    winner: null,
    battleType: '1v1',
    aiAnalysis: null,
    skillMatchPercentage: null
  });

  // UI states
  const [testCases, setTestCases] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('javascript');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showHint, setShowHint] = useState(false);
  const [showAiInsights, setShowAiInsights] = useState(false);
  const [compilerOutput, setCompilerOutput] = useState(null);
  const [activeTab, setActiveTab] = useState('problem'); // problem, submissions, leaderboard
  const [runningTest, setRunningTest] = useState(false);
  const [testResults, setTestResults] = useState([]);

  // Matchmaking states
  const [showMatchmakingModal, setShowMatchmakingModal] = useState(true);
  const [matchmaking, setMatchmaking] = useState(false);
  const [matchFound, setMatchFound] = useState(false);
  const [selectedDifficulty, setSelectedDifficulty] = useState('medium');
  const [selectedCategory, setSelectedCategory] = useState('algorithms');
  const [matchStartError, setMatchStartError] = useState(null);
  const [queuePosition, setQueuePosition] = useState(null);
  const [usersInQueue, setUsersInQueue] = useState(0);

  // Add to the state declarations at the top
  const [isDemoMode, setIsDemoMode] = useState(false);

  // Add state to track the current view
  const [currentView, setCurrentView] = useState('main'); // 'main', 'matchmaking', 'practice', 'battle'

  // Judge0 API configuration
  const JUDGE0_API_URL = 'https://judge0-ce.p.rapidapi.com';
  const JUDGE0_API_KEY = process.env.REACT_APP_JUDGE0_API_KEY || 'your-rapidapi-key';

  // Judge0 language IDs
  const languageIds = {
    javascript: 63,  // Node.js
    python: 71,      // Python 3
    java: 62,        // Java
    cpp: 54,         // C++
    csharp: 51       // C#
  };

  // Language file extensions
  const languageExtensions = {
    javascript: 'js',
    python: 'py',
    java: 'java',
    cpp: 'cpp',
    csharp: 'cs'
  };

  // Editor themes by language
  const editorThemes = {
    javascript: 'vs-dark',
    python: 'vs-dark',
    java: 'vs-dark',
    cpp: 'vs-dark',
    csharp: 'vs-dark'
  };

  // Initialize socket connection
  useEffect(() => {
    // Connect to the Socket.io server
    socketRef.current = io('http://localhost:5000', {
      withCredentials: true,
      transports: ['websocket']
    });

    // Socket event listeners
    socketRef.current.on('connect', () => {
      console.log('Connected to matchmaking server');
    });

    socketRef.current.on('connect_error', (error) => {
      console.error('Socket connection error:', error);
      setMatchStartError('Failed to connect to matchmaking server. Please try again.');
    });

    socketRef.current.on('queue_position', (data) => {
      setQueuePosition(data.position);
      setUsersInQueue(data.totalUsers);
    });

    socketRef.current.on('match_found', (data) => {
      setMatchmaking(false);
      setMatchFound(true);

      // Set opponent data received from server
      setBattleState(prev => ({
        ...prev,
        opponent: {
          name: data.opponent.username,
          rating: data.opponent.rating,
          winRate: data.opponent.winRate,
          preferredLanguage: data.opponent.preferredLanguage,
          skillLevel: `${data.matchPercentage}% match for your skill level`,
          avatar: data.opponent.avatar || "https://randomuser.me/api/portraits/men/32.jpg"
        },
        skillMatchPercentage: `${data.matchPercentage}%`
      }));
    });

    socketRef.current.on('battle_started', (data) => {
      setShowMatchmakingModal(false);

      // Set battle data received from server
      setBattleState(prev => ({
        ...prev,
        status: 'active',
        problem: data.problem,
        userCode: data.problem.starterCode[selectedLanguage] || ''
      }));

      // Set test cases
      setTestCases(data.testCases);

      // Navigate to the battle room if needed
      if (data.battleId && !battleId) {
        navigate(`/battle/${data.battleId}`, { replace: true });
      }
    });

    socketRef.current.on('opponent_progress', (data) => {
      setBattleState(prev => ({
        ...prev,
        opponentProgress: data.progress
      }));
    });

    socketRef.current.on('submission_results', (data) => {
      setIsSubmitting(false);
      setTestResults(data.results);

      // Calculate progress based on passed test cases
      const passedTests = data.results.filter(result => result.passed).length;
      const totalTests = data.results.length;
      const progress = Math.floor((passedTests / totalTests) * 100);

      // Update progress
      updateProgress(progress);

      // Check if all tests passed
      if (data.passed) {
        // Show success message
        setCompilerOutput({
          status: 'success',
          message: 'All test cases passed! You completed the challenge.'
        });
      } else {
        // Show failure message
        setCompilerOutput({
          status: 'error',
          message: 'Some test cases failed. Keep trying!'
        });
      }
    });

    socketRef.current.on('battle_completed', (data) => {
      setBattleState(prev => ({
        ...prev,
        status: 'completed',
        winner: data.winner,
        aiAnalysis: data.aiAnalysis
      }));
    });

    socketRef.current.on('opponent_disconnected', () => {
      // Handle opponent disconnection
      alert('Your opponent has disconnected. You win by default!');
      setBattleState(prev => ({
        ...prev,
        status: 'completed',
        winner: 'user'
      }));
    });

    // Clean up on component unmount
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [battleId, navigate, selectedLanguage]);

  // Initialize battle when component mounts
  useEffect(() => {
    // If battleId is provided, fetch battle details
    if (battleId) {
      fetchBattleDetails(battleId);
    }
  }, [battleId]);

  // Update editor content when language changes
  useEffect(() => {
    if (battleState.problem && battleState.problem.starterCode) {
      setBattleState(prev => ({
        ...prev,
        userCode: battleState.problem.starterCode[selectedLanguage] || ''
      }));
    }
  }, [selectedLanguage, battleState.problem]);

  // Handle editor mount
  function handleEditorDidMount(editor, monaco) {
    editorRef.current = editor;
  }

  // Fetch battle details if joining an existing battle
  const fetchBattleDetails = async (id) => {
    try {
      // Join the battle room
      socketRef.current.emit('join_battle', { battleId: id });

      // The server will respond with battle_started event
      // which is handled in the socket event listeners
    } catch (error) {
      console.error("Error fetching battle details:", error);
      setMatchStartError("Failed to load battle details. Please try again.");
    }
  };

  // Start matchmaking process
  const startMatchmaking = () => {
    setMatchmaking(true);
    setMatchStartError(null);

    // Emit matchmaking request to server
    socketRef.current.emit('find_match', {
      difficulty: selectedDifficulty,
      category: selectedCategory,
      language: selectedLanguage
    });
  };

  // Accept the matched opponent and start the battle
  const acceptMatch = () => {
    setIsSubmitting(true);
    setMatchStartError(null);

    // Emit match acceptance to server
    socketRef.current.emit('accept_match');

    // Set a timeout to handle potential server response delays
    setTimeout(() => {
      if (showMatchmakingModal) {
        console.log("Server didn't respond to match acceptance, forcing battle start");
        setShowMatchmakingModal(false);

        // Initialize a default battle if the server doesn't respond
        if (!battleState.problem) {
          setBattleState(prev => ({
            ...prev,
            status: 'active',
            problem: {
              id: 1,
              title: "Two Sum",
              description: "Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.",
              difficulty: selectedDifficulty,
              category: selectedCategory,
              starterCode: {
                javascript: "/**\n * @param {number[]} nums\n * @param {number} target\n * @return {number[]}\n */\nfunction twoSum(nums, target) {\n  // Your code here\n}",
                python: "def two_sum(nums, target):\n    # Your code here\n    pass",
                java: "class Solution {\n    public int[] twoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}",
                cpp: "#include <vector>\n\nclass Solution {\npublic:\n    std::vector<int> twoSum(std::vector<int>& nums, int target) {\n        // Your code here\n        return {0, 0};\n    }\n};",
                csharp: "public class Solution {\n    public int[] TwoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}"
              }
            },
            userCode: prev.userCode || getDefaultStarterCode(selectedLanguage)
          }));

          // Set default test cases
          setTestCases([
            {
              id: 1,
              input: "[2, 7, 11, 15], 9",
              expectedOutput: "[0,1]"
            },
            {
              id: 2,
              input: "[3, 2, 4], 6",
              expectedOutput: "[1,2]"
            },
            {
              id: 3,
              input: "[3, 3], 6",
              expectedOutput: "[0,1]"
            }
          ]);
        }
      }
      setIsSubmitting(false);
    }, 3000);
  };

  // Decline the match and return to matchmaking
  const declineMatch = () => {
    // Emit match decline to server
    socketRef.current.emit('decline_match');

    setMatchFound(false);
    setMatchmaking(false);
    setMatchStartError(null);
  };

  // Run code against a single test case
  const runTestCase = async (testCaseId) => {
    if (!editorRef.current) return;

    setRunningTest(true);
    setCompilerOutput(null);

    try {
      const code = editorRef.current.getValue();
      const testCase = testCases.find(tc => tc.id === testCaseId);

      if (!testCase) {
        throw new Error('Test case not found');
      }

      // Prepare code with test case
      const preparedCode = prepareCodeWithTestCase(code, testCase);

      // Submit to Judge0
      const result = await submitToJudge0(preparedCode);

      // Process result
      if (result.status.id <= 3) { // Accepted, Wrong Answer, Time Limit Exceeded
        const output = result.stdout || result.stderr || 'No output';
        const expectedOutput = testCase.expectedOutput;
        const passed = output.trim() === expectedOutput.trim();

        setTestResults(prev => {
          const newResults = [...prev];
          const existingIndex = newResults.findIndex(r => r.testCaseId === testCaseId);

          if (existingIndex >= 0) {
            newResults[existingIndex] = { testCaseId, passed, output };
          } else {
            newResults.push({ testCaseId, passed, output });
          }

          return newResults;
        });

        setCompilerOutput({
          status: passed ? 'success' : 'error',
          message: passed ? 'Test passed!' : 'Test failed',
          output,
          expectedOutput
        });
      } else {
        // Compilation error or other error
        setCompilerOutput({
          status: 'error',
          message: result.status.description,
          output: result.stderr || result.compile_output || 'No error details available'
        });
      }
    } catch (error) {
      console.error('Error running test case:', error);
      setCompilerOutput({
        status: 'error',
        message: 'Failed to run test case',
        output: error.message
      });
    } finally {
      setRunningTest(false);
    }
  };

  // Submit code for testing all test cases
  const submitCode = async () => {
    if (!editorRef.current) return;

    setIsSubmitting(true);
    setCompilerOutput(null);

    try {
      const code = editorRef.current.getValue();

      // Update user code in state
      setBattleState(prev => ({
        ...prev,
        userCode: code
      }));

      if (isDemoMode) {
        // In demo mode, handle code evaluation locally
        handleDemoModeSubmission(code);
      } else {
        // In real mode, emit to server
        socketRef.current.emit('submit_code', {
          code,
          language: selectedLanguage
        });

        // For demo purposes, simulate if no API key
        if (!process.env.REACT_APP_JUDGE0_API_KEY) {
          simulateCodeSubmission(code);
        }
      }
    } catch (error) {
      console.error('Error submitting code:', error);
      setIsSubmitting(false);
      setCompilerOutput({
        status: 'error',
        message: 'Failed to submit code',
        output: error.message
      });
    }
  };

  // Add this function to handle demo mode submissions
  const handleDemoModeSubmission = async (code) => {
    // Simulate processing delay
    setTimeout(async () => {
      try {
        // Run code against all test cases
        const results = [];

        for (const testCase of testCases) {
          // Prepare code with test case
          const preparedCode = prepareCodeWithTestCase(code, testCase);

          // If Judge0 API key is available, use it
          let result;
          if (process.env.REACT_APP_JUDGE0_API_KEY) {
            result = await submitToJudge0(preparedCode);

            const output = result.stdout || result.stderr || 'No output';
            const passed = output.trim() === testCase.expectedOutput.trim();

            results.push({
              testCaseId: testCase.id,
              passed,
              output
            });
          } else {
            // Otherwise simulate the result
            // In a real app, you'd use a local code runner or sandbox
            const passed = Math.random() > 0.3; // 70% chance of passing
            results.push({
              testCaseId: testCase.id,
              passed,
              output: passed ? testCase.expectedOutput : 'Incorrect output'
            });
          }
        }

        // Calculate if all tests passed
        const allPassed = results.every(result => result.passed);

        // Calculate progress
        const passedTests = results.filter(result => result.passed).length;
        const progress = Math.floor((passedTests / testCases.length) * 100);

        // Set test results
        setTestResults(results);

        // Set compiler output
        setCompilerOutput({
          status: allPassed ? 'success' : 'error',
          message: allPassed ? 'All test cases passed!' : 'Some test cases failed'
        });

        // If all tests passed in demo mode, check if user won
        if (allPassed && isDemoMode) {
          const opponentProgress = battleState.opponentProgress;

          if (opponentProgress < 100) {
            // User completed before AI
            setTimeout(() => {
              setBattleState(prev => ({
                ...prev,
                status: 'completed',
                winner: 'user',
                aiAnalysis: "Congratulations! You solved the problem before the AI opponent."
              }));
            }, 1000);
          }
        }
      } catch (error) {
        console.error('Error in demo mode submission:', error);
        setCompilerOutput({
          status: 'error',
          message: 'Error evaluating code',
          output: error.message
        });
      } finally {
        setIsSubmitting(false);
      }
    }, 1500);
  };

  // Prepare code with test case for Judge0
  const prepareCodeWithTestCase = (code, testCase) => {
    // Different preparation based on language
    switch (selectedLanguage) {
      case 'javascript':
        return `${code}\n\n// Test case\nconst result = twoSum(${testCase.input});\nconsole.log(JSON.stringify(result));`;

      case 'python':
        return `${code}\n\n# Test case\nresult = two_sum(${testCase.input})\nprint(result)`;

      case 'java':
        return `${code}\n\npublic class Main {\n  public static void main(String[] args) {\n    Solution solution = new Solution();\n    int[] result = solution.twoSum(${testCase.input});\n    System.out.println(java.util.Arrays.toString(result));\n  }\n}`;

      case 'cpp':
        return `${code}\n\nint main() {\n  Solution solution;\n  auto result = solution.twoSum(${testCase.input});\n  for (int i = 0; i < result.size(); i++) {\n    std::cout << result[i];\n    if (i < result.size() - 1) std::cout << ",";\n  }\n  return 0;\n}`;

      case 'csharp':
        return `${code}\n\npublic class Program {\n  public static void Main() {\n    Solution solution = new Solution();\n    int[] result = solution.TwoSum(${testCase.input});\n    Console.WriteLine(string.Join(",", result));\n  }\n}`;

      default:
        return code;
    }
  };

  // Submit code to Judge0 API
  const submitToJudge0 = async (code) => {
    try {
      // Create submission
      const createResponse = await axios.post(`${JUDGE0_API_URL}/submissions`, {
        source_code: code,
        language_id: languageIds[selectedLanguage],
        stdin: '',
        expected_output: ''
      }, {
        headers: {
          'X-RapidAPI-Key': JUDGE0_API_KEY,
          'X-RapidAPI-Host': 'judge0-ce.p.rapidapi.com',
          'Content-Type': 'application/json'
        }
      });

      const token = createResponse.data.token;

      // Poll for results
      let result;
      let attempts = 0;
      const maxAttempts = 10;

      while (attempts < maxAttempts) {
        await new Promise(resolve => setTimeout(resolve, 1000));

        const getResponse = await axios.get(`${JUDGE0_API_URL}/submissions/${token}`, {
          headers: {
            'X-RapidAPI-Key': JUDGE0_API_KEY,
            'X-RapidAPI-Host': 'judge0-ce.p.rapidapi.com'
          },
          params: {
            base64_encoded: 'false',
            fields: 'stdout,stderr,status_id,compile_output,message,time,memory'
          }
        });

        result = getResponse.data;

        // Check if processing is complete
        if (result.status.id !== 1 && result.status.id !== 2) {
          break;
        }

        attempts++;
      }

      return result;
    } catch (error) {
      console.error('Judge0 API error:', error);
      throw new Error('Failed to compile and run code');
    }
  };

  // Update opponent on user's progress
  const updateProgress = (progress) => {
    // Emit progress update to server
    socketRef.current.emit('update_progress', {
      progress: progress
    });
  };

  // Format time for display
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Add this function to start a demo battle
  const startDemoMode = () => {
    console.log("Starting Practice Mode...");
    setIsDemoMode(true);
    setShowMatchmakingModal(false);

    // Initialize a demo battle with a sample problem
    setBattleState(prev => ({
      ...prev,
      status: 'active',
      problem: {
        id: 1,
        title: "Two Sum",
        description: "Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.\n\nYou may assume that each input would have exactly one solution, and you may not use the same element twice.\n\nYou can return the answer in any order.",
        difficulty: selectedDifficulty || 'medium',
        category: selectedCategory || 'algorithms',
        starterCode: {
          javascript: "/**\n * @param {number[]} nums\n * @param {number} target\n * @return {number[]}\n */\nfunction twoSum(nums, target) {\n  // Your code here\n}",
          python: "def two_sum(nums, target):\n    # Your code here\n    pass",
          java: "class Solution {\n    public int[] twoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}",
          cpp: "#include <vector>\n\nclass Solution {\npublic:\n    std::vector<int> twoSum(std::vector<int>& nums, int target) {\n        // Your code here\n        return {0, 0};\n    }\n};",
          csharp: "public class Solution {\n    public int[] TwoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}"
        }
      },
      opponent: {
        name: "AI Opponent",
        rating: 1800,
        winRate: "75%",
        preferredLanguage: "Python",
        skillLevel: "Adaptive",
        avatar: "https://randomuser.me/api/portraits/lego/1.jpg"
      },
      userCode: getDefaultStarterCode(selectedLanguage || 'javascript')
    }));

    // Set demo test cases
    setTestCases([
      {
        id: 1,
        input: "[2, 7, 11, 15], 9",
        expectedOutput: "[0,1]"
      },
      {
        id: 2,
        input: "[3, 2, 4], 6",
        expectedOutput: "[1,2]"
      },
      {
        id: 3,
        input: "[3, 3], 6",
        expectedOutput: "[0,1]"
      }
    ]);

    // Simulate AI opponent progress updates
    startAIOpponentSimulation();
  };

  // Add this function to simulate AI opponent progress
  const startAIOpponentSimulation = () => {
    let progress = 0;
    const interval = setInterval(() => {
      if (progress < 100) {
        // Randomly increase progress between 1-5%
        const increment = Math.floor(Math.random() * 5) + 1;
        progress = Math.min(progress + increment, 100);

        // Update opponent progress
        setBattleState(prev => ({
          ...prev,
          opponentProgress: progress
        }));

        // If progress reaches 100%, simulate battle completion after a delay
        if (progress === 100) {
          setTimeout(() => {
            if (isDemoMode) {
              // Check if user has completed the challenge
              const userProgress = testResults.filter(r => r.passed).length / testCases.length * 100;

              if (userProgress === 100) {
                // User won
                setBattleState(prev => ({
                  ...prev,
                  status: 'completed',
                  winner: 'user',
                  aiAnalysis: "Great job! You solved the problem efficiently."
                }));
              } else {
                // AI won
                setBattleState(prev => ({
                  ...prev,
                  status: 'completed',
                  winner: 'opponent',
                  aiAnalysis: "The AI opponent solved the problem faster. Try again!"
                }));
              }
            }
          }, 3000);
        }
      } else {
        clearInterval(interval);
      }
    }, 2000);

    // Clean up interval on component unmount
    return () => clearInterval(interval);
  };

  // Add a dedicated function to render the main menu with clear options
  const renderMainMenu = () => {
    return (
      <div className="coding-battle-main-menu">
        <h1>Coding Battle Arena</h1>
        <p className="main-subtitle">Choose your battle mode</p>

        <div className="battle-mode-cards">
          <div className="mode-card" onClick={() => setShowMatchmakingModal(true)}>
            <div className="mode-icon"></div>
            <h2>Competitive Mode</h2>
            <p>Challenge real opponents in a head-to-head coding battle</p>
            <button className="mode-button competitive">Find Opponent</button>
          </div>

          <div className="mode-card practice-card" onClick={startDemoMode}>
            <div className="mode-icon"></div>
            <h2>Practice Mode</h2>
            <p>Train your skills against an AI opponent at your own pace</p>
            <button className="mode-button practice">Start Practice</button>
          </div>
        </div>

        <div className="battle-stats">
          <div className="stat">
            <span className="stat-number">250+</span>
            <span className="stat-label">Coding Challenges</span>
          </div>
          <div className="stat">
            <span className="stat-number">5</span>
            <span className="stat-label">Programming Languages</span>
          </div>
          <div className="stat">
            <span className="stat-number">24/7</span>
            <span className="stat-label">Available</span>
          </div>
        </div>
      </div>
    );
  };

  // Render the matchmaking modal
  const renderMatchmakingModal = () => {
    return (
      <div className="matchmaking-modal-overlay">
        <div className="matchmaking-modal">
          {!matchmaking && !matchFound ? (
            <>
              <h2>Find Your Perfect Opponent</h2>
              <p className="modal-subtitle">Our AI will match you with an opponent of similar skill level</p>

              <div className="matchmaking-options">
                <div className="option-group">
                  <label>Difficulty Level</label>
                  <div className="option-buttons">
                    <button 
                      className={selectedDifficulty === 'easy' ? 'active' : ''} 
                      onClick={() => setSelectedDifficulty('easy')}
                    >
                      Easy
                    </button>
                    <button 
                      className={selectedDifficulty === 'medium' ? 'active' : ''} 
                      onClick={() => setSelectedDifficulty('medium')}
                    >
                      Medium
                    </button>
                    <button 
                      className={selectedDifficulty === 'hard' ? 'active' : ''} 
                      onClick={() => setSelectedDifficulty('hard')}
                    >
                      Hard
                    </button>
                  </div>
                </div>

                <div className="option-group">
                  <label>Problem Category</label>
                  <div className="option-buttons">
                    <button 
                      className={selectedCategory === 'algorithms' ? 'active' : ''} 
                      onClick={() => setSelectedCategory('algorithms')}
                    >
                      Algorithms
                    </button>
                    <button 
                      className={selectedCategory === 'data-structures' ? 'active' : ''} 
                      onClick={() => setSelectedCategory('data-structures')}
                    >
                      Data Structures
                    </button>
                    <button 
                      className={selectedCategory === 'dynamic-programming' ? 'active' : ''} 
                      onClick={() => setSelectedCategory('dynamic-programming')}
                    >
                      Dynamic Programming
                    </button>
                  </div>
                </div>

                <div className="option-group">
                  <label>Programming Language</label>
                  <div className="option-buttons">
                    <button 
                      className={selectedLanguage === 'javascript' ? 'active' : ''} 
                      onClick={() => setSelectedLanguage('javascript')}
                    >
                      JavaScript
                    </button>
                    <button 
                      className={selectedLanguage === 'python' ? 'active' : ''} 
                      onClick={() => setSelectedLanguage('python')}
                    >
                      Python
                    </button>
                    <button 
                      className={selectedLanguage === 'java' ? 'active' : ''} 
                      onClick={() => setSelectedLanguage('java')}
                    >
                      Java
                    </button>
                  </div>
                </div>
              </div>

              {matchStartError && <p className="error-message">{matchStartError}</p>}

              <div className="modal-actions">
                <button className="primary-button" onClick={startMatchmaking}>
                  Find Match
                </button>
                <button 
                  className="practice-mode-button" 
                  onClick={startDemoMode}
                >
                  Practice Mode
                </button>
                <button className="cancel-button" onClick={() => setShowMatchmakingModal(false)}>
                  Cancel
                </button>
              </div>
            </>
          ) : matchFound ? (
            <div className="match-found-container">
              <h2>Match Found!</h2>
              <div className="opponent-info">
                <img 
                  src={battleState.opponent?.avatar || "https://randomuser.me/api/portraits/men/32.jpg"} 
                  alt="Opponent" 
                  className="opponent-avatar" 
                />
                <div className="opponent-details">
                  <h3>{battleState.opponent?.name || "Unknown Opponent"}</h3>
                  <p>Rating: {battleState.opponent?.rating || "1500"}</p>
                  <p>Win Rate: {battleState.opponent?.winRate || "50%"}</p>
                  <p>Preferred Language: {battleState.opponent?.preferredLanguage || "JavaScript"}</p>
                  <p>Skill Match: {battleState.skillMatchPercentage || "85%"}</p>
                </div>
              </div>
              <div className="modal-actions">
                <button 
                  className="primary-button" 
                  onClick={acceptMatch}
                  disabled={isSubmitting}
                >
                  {isSubmitting ? "Starting..." : "Accept Match"}
                </button>
                <button 
                  className="secondary-button" 
                  onClick={declineMatch}
                  disabled={isSubmitting}
                >
                  Decline
                </button>
              </div>
            </div>
          ) : (
            <div className="matchmaking-progress">
              <h2>Finding Your Match</h2>
              <div className="loading-spinner"></div>
              <p>Searching for opponents with similar skill level...</p>
              {queuePosition && (
                <p className="queue-position">
                  Position in queue: {queuePosition} of {usersInQueue}
                </p>
              )}
              <button className="secondary-button" onClick={() => {
                socketRef.current.emit('cancel_matchmaking');
                setMatchmaking(false);
              }}>
                Cancel
              </button>
            </div>
          )}
        </div>
      </div>
    );
  };

  // Render the main battle UI
  return (
    <div className="coding-battle-container">
      {battleState.status === 'waiting' && currentView === 'main' ? (
        renderMainMenu()
      ) : (
        renderBattleInterface()
      )}

      {showMatchmakingModal && renderMatchmakingModal()}
    </div>
  );
}

export default CodingBattle;

// Helper function to get default starter code based on language
const getDefaultStarterCode = (language) => {
  const defaultCode = {
    javascript: "/**\n * @param {number[]} nums\n * @param {number} target\n * @return {number[]}\n */\nfunction twoSum(nums, target) {\n  // Your code here\n}",
    python: "def two_sum(nums, target):\n    # Your code here\n    pass",
    java: "class Solution {\n    public int[] twoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}",
    cpp: "#include <vector>\n\nclass Solution {\npublic:\n    std::vector<int> twoSum(std::vector<int>& nums, int target) {\n        // Your code here\n        return {0, 0};\n    }\n};",
    csharp: "public class Solution {\n    public int[] TwoSum(int[] nums, int target) {\n        // Your code here\n        return new int[]{0, 0};\n    }\n}"
  };

  return defaultCode[language] || defaultCode.javascript;
};
