import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './pages/HomePage';
import JobBoard from './components/JobBoard';
import CodingBattles from './pages/CodingBattles';
import CodingBattle from './pages/CodingBattle';
import LearningPaths from './pages/LearningPaths';
import Achievements from './pages/Achievements';
import Profile from './pages/Profile';
import Settings from './pages/Settings';
import SignUp from './pages/SignUp';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import './App.css';

function App() {
  // In a real app, this would be managed by an auth context/provider
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle login
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Function to handle logout
  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div className="app-container">
        <Navbar isLoggedIn={isLoggedIn} onLogout={handleLogout} />
        <div className="main-content">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/jobs" element={<JobBoard />} />
            <Route path="/battles" element={<CodingBattles />} />
            <Route path="/battle/:id" element={<CodingBattle />} />
            <Route path="/battle/practice" element={<CodingBattle isDemoMode={true} />} />
            <Route path="/learning" element={<LearningPaths />} />
            <Route path="/achievements" element={
              isLoggedIn ? <Achievements /> : <Navigate to="/" replace />
            } />
            <Route path="/profile" element={
              isLoggedIn ? <Profile /> : <Navigate to="/" replace />
            } />
            <Route path="/settings" element={
              isLoggedIn ? <Settings /> : <Navigate to="/" replace />
            } />
            <Route path="/signup" element={
              !isLoggedIn ? <SignUp onSignUp={handleLogin} /> : <Navigate to="/" replace />
            } />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
