import React, { useEffect, useState } from 'react';
import './CursorEffect.css';

const CursorEffect = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [hidden, setHidden] = useState(true);
  const [clicked, setClicked] = useState(false);
  const [linkHovered, setLinkHovered] = useState(false);
  const [codeHovered, setCodeHovered] = useState(false);
  const [trail, setTrail] = useState([]);

  useEffect(() => {
    // Add event listeners
    const addEventListeners = () => {
      document.addEventListener('mousemove', onMouseMove);
      document.addEventListener('mouseenter', onMouseEnter);
      document.addEventListener('mouseleave', onMouseLeave);
      document.addEventListener('mousedown', onMouseDown);
      document.addEventListener('mouseup', onMouseUp);
    };

    // Track cursor position
    const onMouseMove = (e) => {
      setPosition({ x: e.clientX, y: e.clientY });
      setHidden(false);

      // Add position to trail with timestamp
      setTrail(prevTrail => {
        const now = Date.now();
        const newTrail = [...prevTrail, { x: e.clientX, y: e.clientY, time: now }];

        // Keep only points from the last 100ms for the trail
        return newTrail.filter(point => now - point.time < 100);
      });

      // Check if cursor is over a link or button
      const target = e.target;
      const isLink = target.tagName.toLowerCase() === 'a' || 
                    target.tagName.toLowerCase() === 'button' ||
                    target.closest('a') || 
                    target.closest('button');

      setLinkHovered(isLink);

      // Check if cursor is over code
      const isCode = target.tagName.toLowerCase() === 'code' || 
                    target.closest('code') || 
                    target.closest('.code-challenge');

      setCodeHovered(isCode);
    };

    const onMouseEnter = () => {
      setHidden(false);
    };

    const onMouseLeave = () => {
      setHidden(true);
    };

    const onMouseDown = () => {
      setClicked(true);
    };

    const onMouseUp = () => {
      setClicked(false);
    };

    // Add event listeners
    addEventListeners();

    // Remove event listeners on cleanup
    return () => {
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mouseenter', onMouseEnter);
      document.removeEventListener('mouseleave', onMouseLeave);
      document.removeEventListener('mousedown', onMouseDown);
      document.removeEventListener('mouseup', onMouseUp);
    };
  }, []);

  // Generate trail elements
  const trailElements = trail.map((point, index) => {
    const size = Math.max(5, 15 * (1 - (Date.now() - point.time) / 100));
    const opacity = Math.max(0.1, 0.5 * (1 - (Date.now() - point.time) / 100));

    return (
      <div
        key={`trail-${index}`}
        className="cursor-trail"
        style={{
          left: point.x,
          top: point.y,
          width: `${size}px`,
          height: `${size}px`,
          opacity: opacity
        }}
      />
    );
  });

  // Determine cursor classes based on state
  const cursorClasses = `cursor-dot ${hidden ? 'hidden' : ''} ${clicked ? 'clicked' : ''} ${linkHovered ? 'link-hovered' : ''} ${codeHovered ? 'code-hovered' : ''}`;
  const cursorRingClasses = `cursor-ring ${hidden ? 'hidden' : ''} ${clicked ? 'clicked' : ''} ${linkHovered ? 'link-hovered' : ''} ${codeHovered ? 'code-hovered' : ''}`;

  // Code effect elements for when hovering over code
  const codeEffectElements = codeHovered ? (
    <>
      <div className="code-cursor-effect code-cursor-bracket" style={{ left: position.x + 10, top: position.y - 10 }}>{'{'}</div>
      <div className="code-cursor-effect code-cursor-bracket" style={{ left: position.x - 10, top: position.y + 10 }}>{'}'}</div>
      <div className="code-cursor-effect code-cursor-semicolon" style={{ left: position.x + 15, top: position.y + 5 }}>;</div>
    </>
  ) : null;

  return (
    <>
      {/* Trail elements */}
      {trailElements}

      {/* Main cursor elements */}
      <div className={cursorClasses} style={{ left: position.x, top: position.y }} />
      <div className={cursorRingClasses} style={{ left: position.x, top: position.y }} />

      {/* Code effect elements */}
      {codeEffectElements}
    </>
  );
};

export default CursorEffect;
